import axios from 'axios'
import React, { useState } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';

function App() {
  const [pokemon, setPokemon] = useState(null);
  const [id, setId] = useState();

  const input = (e)=>{
    setId(e.target.value);
  }
  const submit = async (e)=>{
    const url = `https://pokeapi.co/api/v2/pokemon/${id}`;
    try {
      const respons = await axios.get(url)
      setPokemon(respons.data)
    } catch (err) {
      console.log(err)
    }
  }
  return (
    <>
    <div className='w-100 bg-dark d-flex justify-content-center align-items-center' style={{height:'75vh'}}>
    <div>
      <h1 className='text-light text-center display-4 fw-bold mb-5'>Pokedex App</h1>
    <form onSubmit={submit} className='d-flex'>
      <input type="text" className='form-control p-2' onChange={input} placeholder='Enter A Number For Pokemon'/>
      <button className='btn btn-outline-light mx-2'>Get</button>
    </form>
    {
      pokemon &&(
        <div className='text-light'>
          <h2>Your Choice</h2>
          <h3>{pokemon.name}</h3>
          <img src={pokemon.sprites.front_default}/>
        </div>
      )
    }
    </div>
    </div>
    </>
  )
}

export default App
