import axios from 'axios';
import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';

function App() {
  const [pokemon, setPokemon] = useState(null);
  const [id, setId] = useState('');

  const handleInputChange = (event) => {
    setId(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const url = `https://pokeapi.co/api/v2/pokemon/${id}`;
    try {
      const response = await axios.get(url);
      setPokemon(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className='w-100 bg-dark d-flex justify-content-center align-items-center' style={{ height: '100vh' }}>
      <div>
        <h1 className='text-light text-center display-4 fw-bold mb-5'>Pokedex App</h1>
        <form onSubmit={handleSubmit} className='d-flex' action="">
          <input
            type="text"
            className='form-control p-2'
            value={id}
            onChange={handleInputChange}
            placeholder='Enter A Number For Pokemon'
          />
          <button type='submit' className='btn btn-outline-light mx-2'>Get</button>
        </form>
        {pokemon && (
          <div className='text-light'>
            <h2>Your Choice</h2>
            <h3>{pokemon.name}</h3>
            <img src={pokemon.sprites.front_default} alt={pokemon.name} />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
